import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
    name:"cart-slice" ,
    initialState : { items:[] } ,
    reducers: { 
        addItemToCart(state , action){
            const findItem = state.items.findIndex((item)=> item.id == action.id) ;

            if(findItem > -1){
                let UpdatedItems = [...state.items] ;
                let existingItem = UpdatedItems[findItem] ;
                let updatedItem = {
                    ...existingItem ,
                    quantity : existingItem.quantity + 1 
                }

                existingItem = updatedItem ;
            }

            else {
                let UpdatedItems = [...state.items] ;
                UpdatedItems.push({
                    ...action,
                    quantity : 1
                })
            }

            return {...state , items :UpdatedItems } ;
        } ,

        removeItemFromCart(state , action){
            
            const findItem = state.items.findIndex((item)=> item.id == action.id) ;
            const UpdatedItems = [...state.items] ;
            console.log(findItem)

            if(findItem > -1){
                const existingItem = UpdatedItems[findItem] ;
                let updatedItem = {
                    ...existingItem ,
                    quantity : existingItem.quantity - 1 
                }
                UpdatedItems[findItem] = updatedItem ;
            }
            else if(UpdatedItems[findItem].quantity === 1){
                UpdatedItems.splice(findItem,1);
                console.log(findItem)

            }

            return {...state ,items :UpdatedItems }
        }


    }
})

export const actionsCart = cartSlice.actions ;

export default cartSlice.reducer ;
